import React, { useState } from "react";
import InputSender from "./InputSender";
import Keyboard from "./keyboard";
import PlayPauseButton from "./play";
import Touchpad from "./touchpad";

const COMPONENTS = {
  input: { label: "Текстовое поле", component: <InputSender /> },
  keyboard: { label: "Клавиатура", component: <Keyboard /> },
  play: { label: "Плеер", component: <PlayPauseButton /> },
  touchpad: { label: "Тачпад", component: <Touchpad /> },
};

const Header = () => {
  const [open, setOpen] = useState(false);
  const [visible, setVisible] = useState({});

  const toggleComponent = (key) => {
    setVisible((prev) => ({ ...prev, [key]: !prev[key] }));
  };

  return (
    <div
      style={{
        width: "100%",
        background: "linear-gradient(90deg, #162447 0%, #1f4068 100%)",
        color: "#b3cfff",
        position: "relative",
        boxShadow: "0 2px 12px 0 #0f2027cc",
        borderBottom: "2px solid #1976d2",
        fontFamily: "'Orbitron', 'Segoe UI', Arial, sans-serif",
        letterSpacing: "0.07em",
      }}
    >
      <div
        style={{
          padding: "16px 24px",
          fontSize: 26,
          cursor: "pointer",
          userSelect: "none",
          display: "flex",
          alignItems: "center",
          fontWeight: 700,
          textShadow: "0 2px 8px #0f2027",
        }}
        onClick={() => setOpen((v) => !v)}
      >
        ☰ Меню
      </div>
      {open && (
        <div
          style={{
            position: "absolute",
            left: 0,
            top: "100%",
            background: "linear-gradient(90deg, #1b2a49 0%, #23395d 100%)",
            color: "#b3cfff",
            boxShadow: "0 4px 16px 0 #0f2027cc",
            zIndex: 10,
            minWidth: 240,
            borderRadius: "0 0 12px 12px",
            padding: "8px 0",
            border: "1px solid #1976d2",
          }}
        >
          {Object.entries(COMPONENTS).map(([key, { label }]) => (
            <button
              key={key}
              onClick={() => toggleComponent(key)}
              style={{
                width: "100%",
                padding: "14px 28px",
                background: visible[key]
                  ? "linear-gradient(90deg, #1976d2 0%, #0f2027 100%)"
                  : "transparent",
                color: visible[key] ? "#fff" : "#b3cfff",
                border: "none",
                textAlign: "left",
                fontSize: 19,
                cursor: "pointer",
                outline: "none",
                fontWeight: 600,
                letterSpacing: "0.06em",
                transition: "background 0.2s, color 0.2s",
                borderBottom: "1px solid #23395d",
              }}
            >
              {label}
            </button>
          ))}
        </div>
      )}
      {/* Разворачиваем выбранные компоненты */}
      <div style={{ marginTop: open ? 16 : 0 }}>
        {Object.entries(COMPONENTS).map(
          ([key, { component }]) =>
            visible[key] && (
              <div
                key={key}
                style={{
                  margin: "24px 0",
                  background: "rgba(31, 64, 104, 0.85)",
                  borderRadius: 14,
                  boxShadow: "0 4px 24px 0 #0f2027cc",
                  padding: "24px 18px",
                  maxWidth: 700,
                  marginLeft: "auto",
                  marginRight: "auto",
                  border: "1.5px solid #1976d2",
                }}
              >
                {component}
              </div>
            )
        )}
      </div>
    </div>
  );
};

export default Header;
